Script.Signals = {
    didMount() {
        console.log(Script.Inputs.Color1);
        const domElement = Script.Inputs.TextNode.getDOMElement()
        const nodeStyle = domElement.style;
        // Construct the CSS linear-gradient property
        const gradient = `linear-gradient(${Script.Inputs.Direction || 0}, ` +
                         `${Script.Inputs.Color1} -50%, ` +
                         `${Script.Inputs.Color2} 100%)`;
        console.log(gradient);
        // Apply the gradient directly to the background style
        nodeStyle.background = gradient;

        // Ensure the gradient is clipped to the text
        nodeStyle.webkitBackgroundClip = 'text'; // For Webkit browsers
        nodeStyle.webkitTextFillColor = 'transparent';
    },
    willUnmount() {
