Outputs.Items = Inputs.Items.map(i => ({
    Label:i.Label,
    Url:i.Url,
    Icon:i.Icon,
    // Set the "selected" item as the one with matching path name, or it it's an empty pathname
    // the IsHome item
    Selected:Inputs.CurrentPathName !== undefined && (Inputs.CurrentPathName === '/' && i.IsHome) || (Inputs.CurrentPathName !== '/' && i.Url.startsWith(Inputs.CurrentPathName))
}))

const onUrlChange = () => {
    // Send the current pathname as an output
    Outputs.PathName = document.location.pathname
}

// Detect a change of the pathname
if (window.history && window.history.pushState) {
    window.addEventListener('popstate', onUrlChange)
} else {
    window.addEventListener('hashchange', onUrlChange)
}

onUrlChange()

Outputs.IconSource = {
    "class": "material-icons",
    "code": Inputs.IconCode
}
Outputs.HaveIcon = Inputs.IconCode !== undefined && Inputs.IconCode !== ""

