if(Inputs.generateColor){
    const palette = Inputs.Colors;
    const text = Inputs.Text;

    function hashCode(str) {
        var hash = 0, i, chr;
        if (str.length === 0) return hash;
        for (i = 0; i < str.length; i++) {
            chr = str.charCodeAt(i);
            hash = (hash << 5) - hash + chr;
            hash |= 0;
        }
        return hash;
    }

    Outputs.Color = palette[hashCode(text) % palette.length];
} 

else{
    Outputs.Color = Inputs.defaultColor;
}

